<?php function checkUploadedFile($file){	$allowedExts = array("jpg", "jpeg", "pdf", "png");	$file_name = $file["name"];	$extension = explode(".", $file_name);	if (in_array($extension[1], $allowedExts)){		if ($file["error"] > 0){			return false;		}		else{			if(move_uploaded_file($file["tmp_name"],"uploadcv/".date('Ydh'). $file["name"]))				return "uploadcv/" . $file["name"];			else				return false;	    	}  	}	else  		return false;}if(isset($_POST['name']) && isset($_POST['family_name']) && isset($_POST['email']) && filter_var($_POST['email'],FILTER_VALIDATE_EMAIL)){	$senderName		= filter_var($_POST['name'],FILTER_SANITIZE_STRING);	$family_name	= filter_var($_POST['family_name'],FILTER_SANITIZE_STRING);	$email          = filter_var($_POST['email'],FILTER_SANITIZE_EMAIL);	$facebook       = filter_var($_POST['facebook'],FILTER_SANITIZE_STRING);	$twitter        = filter_var($_POST['twitter'],FILTER_SANITIZE_STRING);	$message		= filter_var($_POST['message'],FILTER_SANITIZE_STRING);	$cv	= $_FILES['cv'];	if($uploadedCv=checkUploadedFile($cv)){		$siteName		= "Gideas Jobs";		$to 			= "e.morales@altusmedia.com.mx";		$headers  		= "MIME-Version: 1.0\r\n"; 		$headers 		.= "Content-type: text/html; charset=iso-8859-1\r\n";		$headers 		.= "From: " . $siteName ." \n";		$headers 		.= "Reply-To: " . $email . "\n\n";		$toSubject 		= "Mensaje de $senderName via $siteName";		$emailBody 		= "<strong>From</strong>: $senderName <br />						<strong>Email</strong>: $email <br /> 												<strong>Mensaje</strong>: <br /><br />						". nl2br($message)."<br />Su curriculum se encuentra disponible en: ".$_SERVER['HTTP_REFERER'].$uploadedCv;  		$message 		= $emailBody;		$okMsg = "";		if( $to != "your_email@provider.com" )		    $ok 		= mail($to, $toSubject, $message, $headers);		else		   echo json_encode("error 404");				    		if($ok)			echo json_encode('Tus datos se han enviado correctamente.');		else			echo json_encode('Por el momento nuestro servidor se encuentra ocupado.');	}	else		echo json_encode('Tipo de archivo no permitido.');}else	echo json_encode('Los campos marcados con * son obligatorios');	?>